mapper.select:
SELECT * 
FROM Posts 
WHERE member_id= #{member_id}

mapper.update:

UPDATE Posts
SET  

        post_title = #{post_title}


        , post_contents = #{post_contents}


        , post_up_sport = #{post_up_sport}


        , post_sport = #{post_sport}


        , post_sports_keyword = #{post_sports_keyword}


        , post_img1 = #{post_img1}


        , post_img2 = #{post_img2}


        , post_hashtag = #{post_hashtag}

    , post_modify_date = now()
WHERE
    post_id = #{post_id} 
    AND    member_id = #{member_id}

mapper.delete:
delete from Posts
WHERE 
    post_id = #{post_id}
AND 
    member_id=#{member_id}

mapper.select:
SELECT * 
FROM Posts 
WHERE post_id= #{post_id}

SELECT @rownum := @rownum +1 As rownum, Posts.* From(SELECT p.post_id AS post_id, p.post_title AS post_title, p.post_sport AS post_sport, p.post_date AS post_date, p.post_up_sport AS post_up_sport, p.post_sports_keyword AS sport_keyword, p.post_like_counts AS post_like_counts, p.post_views AS post_views, p.post_hashtag AS post_hashtag, COUNT(c.comments_id) AS comments_count, m.nickname AS post_nickname, m.member_id AS member_id FROM Posts p LEFT JOIN Comments c ON p.post_id = c.post_id JOIN Member m ON p.member_id = m.member_id WHERE
p.post_up_sport = #{post_up_sport}
AND p.post_sport = #{post_sport}
GROUP BY p.post_id, p.post_title, p.post_sport, p.post_sports_keyword, p.post_date, p.post_up_sport, p.post_like_counts, p.post_views, m.nickname, m.member_id ORDER BY p.post_date DESC LIMIT #{size} OFFSET #{offset}) Posts, (SELECT @rownum := 0 )r

SELECT COUNT(*)
FROM Posts p
WHERE 1=1

        AND p.post_up_sport= #{post_up_sport}


        AND p.post_sport = #{post_sport}

mapper.update:
UPDATE PostRecommendations
SET is_clicked = true
WHERE member_id = #{member_id}
    AND 
        post_id = #{post_id}

mapper.select:
SELECT p.post_id AS post_id, p.post_title AS post_title, p.post_sport AS post_sport, p.post_date AS post_date, p.post_sports_keyword AS post_sports_keyword, p.post_like_counts AS post_like_counts, p.post_views AS post_views, p.post_hashtag AS post_hashtag, COUNT(c.comments_id) AS comments_count, m.nickname AS post_nickname, m.member_id AS member_id, (p.post_like_counts*1.0 + post_views*0.5) AS score, (SELECT pr.is_clicked FROM PostRecommendations pr WHERE pr.post_id = p.post_id AND pr.member_id = #{member_id}) AS is_clicked FROM Posts p LEFT JOIN Comments c ON p.post_id = c.post_id JOIN Member m ON p.member_id = m.member_id WHERE
p.post_sport = #{post_sport} AND post_up_sport = #{post_up_sport}
AND p.post_id NOT IN
#{postId}
GROUP BY p.post_id, p.post_title, p.post_sport, p.post_sports_keyword, p.post_date, p.post_like_counts, p.post_views, m.nickname, m.member_id ORDER BY score DESC LIMIT #{limit}

mapper.insert:

INSERT INTO PostRecommendations (post_id, member_id)
SELECT #{post_id}, #{member_id}
FROM DUAL
WHERE NOT EXISTS (
    SELECT 1
    FROM PostRecommendations
    WHERE post_id = #{post_id} AND member_id = #{member_id}
)

