mapper.select:
SELECT *
FROM Crew c
WHERE c.crew_state = 1
    AND c.crew_location = (
        SELECT profile_region
        FROM UserProfile    
        WHERE member_id = #{member_id}
    )
ORDER BY c.crew_date DESC;

SELECT *
FROM Crew
WHERE crew_state = 1
    AND crew_location LIKE(
        SELECT CONCAT(SUBSTRING_INDEX(profile_region, ',', 1), '%')
        FROM UserProfile    
        WHERE member_id = #{member_id}
    )
ORDER BY crew_date DESC;

SELECT *
FROM Crew c
WHERE c.crew_state = 1
    AND c.crew_location = (
        SELECT profile_region
        FROM UserProfile    
        WHERE member_id = #{member_id}
    )
    OR c.crew_location = (
        SELECT profile_region
        FROM UserProfile
        WHERE member_id = #{member_id}
    )
ORDER BY c.crew_date DESC
LIMIT 3;

SELECT *
FROM Crew
WHERE crew_state = 1
    AND crew_location LIKE(
        SELECT CONCAT(SUBSTRING_INDEX(profile_region, ',', 1), '%')
        FROM UserProfile    
        WHERE member_id = #{member_id}
    )
ORDER BY crew_date DESC
LIMIT 3;

mapper.update:
UPDATE Crew
SET crew_intro_post = #{crew_intro_post},
    crew_intro_img = #{crew_intro_img},
    crew_name = #{crew_name}
WHERE crew_id = #{crew_id}

mapper.select:
SELECT member_id
FROM Crew
WHERE crew_id = #{crew_id};

mapper.update:
UPDATE CrewMember
SET crew_member_state = 1
WHERE crew_id = #{crew_id} AND member_id = #{member_id};

mapper.select:
SELECT cp.*, m.nickname
FROM CrewPost cp
LEFT JOIN Member m ON cp.member_id = m.member_id
WHERE cp.crew_id = #{crew_id}
ORDER BY cp.crew_post_date DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset};

SELECT COUNT(*)
FROM CrewPost
WHERE crew_id = #{crew_id};

Select *
FROM CrewPost
WHERE crew_id = #{crew_id} AND crew_post_state = #{crew_post_state}
ORDER BY crew_post_date DESC limit 3;

Select cp.*, m.nickname
FROM CrewPost cp
LEFT JOIN Member m ON cp.member_id = m.member_id
WHERE cp.crew_id = #{crew_id} AND cp.crew_post_state = #{crew_post_state}
ORDER BY cp.crew_post_date DESC;

SELECT *
FROM CrewPostLike
WHERE crew_post_id = #{crew_post_id} AND member_id = #{member_id};

mapper.update:
UPDATE CrewPost
SET crew_post_like = crew_post_like + 1
WHERE crew_post_id = #{crew_post_id};

UPDATE CrewPost
SET crew_post_like = crew_post_like - 1
WHERE crew_post_id = #{crew_post_id};

mapper.insert:
INSERT INTO CrewPostLike(
    crew_post_id,
    member_id,
    crew_id
) VALUES (
    #{crew_post_id},
    #{member_id},
    #{crew_id}
);

mapper.update:
UPDATE CrewPost
SET crew_post_state = 1
WHERE crew_post_id = #{crew_post_id} AND crew_post_state = 2;

mapper.select:
SELECT COUNT(*)
FROM CrewMember
WHERE crew_id = #{crew_id} AND crew_member_state = 1;

SELECT crew_post_like
FROM CrewPost
WHERE crew_post_id = #{crew_post_id};

mapper.insert:
INSERT INTO CrewComments(
    crew_comments_content,
    crew_post_id,
    member_id
) VALUES (
    #{crew_comments_content},
    #{crew_post_id},
    #{member_id}
);

mapper.select:
SELECT *
FROM CrewComments
WHERE crew_post_id = #{crew_post_id}
ORDER BY crew_comments_date ASC;
