mapper.select:
SELECT *
FROM Crew
WHERE crew_id = #{crew_id} AND crew_state = 1;

mapper.update:
UPDATE Crew
SET crew_intro_post = #{crew_intro_post},
    crew_intro_img = #{crew_intro_img},
    crew_name = #{crew_name}
WHERE crew_id = #{crew_id}

UPDATE CrewMember
SET crew_member_state = 1
WHERE crew_id = #{crew_id} AND member_id = #{member_id};

mapper.select:
SELECT *
FROM CrewPost
WHERE crew_id = #{crew_id}
ORDER BY crew_post_date DESC;

Select *
FROM CrewPost
WHERE crew_id = #{crew_id} AND crew_post_state = #{crew_post_state}
ORDER BY crew_post_date DESC limit 3;

Select *
FROM CrewPost
WHERE crew_id = #{crew_id} AND crew_post_state = #{crew_post_state}
ORDER BY crew_post_date DESC;

SELECT *
FROM CrewPost
WHERE crew_id = #{crew_id} AND crew_post_id = #{crew_post_id}
ORDER BY crew_post_date DESC;

mapper.insert:
INSERT INTO CrewComments(
    crew_comments_content,
    crew_comments_date,
    crew_post_id,
    member_id
) VALUES (
    #{crew_comments_content},
    #{crew_comments_date},
    #{crew_post_id},
    #{member_id}
);

mapper.select:
SELECT *
FROM CrewComments
WHERE crew_post_id = #{crew_post_id} AND member_id = #{member_id}
ORDER BY crew_comments_date ASC;
